import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function Homepage() {

    const navigate = useNavigate();
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // fetch the data
        // display it on "recent-boxing-news"

        const fetchPosts = async () => {
            try {
                const fetchedPosts = fetch("http://localhost:3001/posts");
                setPosts(fetchedPosts);
            }
            catch(error) {
                console.log("Error setting posts equal to fetched posts : ", error);
            }
        };
        fetchPosts();
    }, []); 

    const handleCNPPageButtonClick = async () => {
        try {
            navigate("/createPost");
        } catch (error) {
            console.error("Error during navigating to CNP page :", error);
        }
    };

    return (
        <div>
            <button id="go-to-create-new-post-page-button" onClick = {handleCNPPageButtonClick}>Create New Post</button>
            <h1 id = "recent-boxing-news">Recent Boxing News</h1>

            <div>
                <div>POSTS</div>
                {posts.map((post) => {
                    return(
                    <div key = {post._id}>
                        <h2>{post.title}</h2>
                        <p>{post.description}</p>
                    </div>
                    );
                })}

            </div>
        </div>
    );
}

export default Homepage;
