import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


function Login() {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();


    // const handleButtonClick = async () => {
    //     try{
    //         const response = await fetch("http://localhost:3001/login", {
    //             method: "POST",
    //             headers: { "Content-Type": "application/json" },
    //             body: JSON.stringify({
    //                 username: username,
    //                 password: password
    //             })
    //         });
    
    //         console.log("1");
    //         const responseJSON = await response.json();
    //         console.log("2");
    
    //         if(responseJSON.success === true) {
    //             console.log("login succeeded");
    
    //                 navigate("/homepage");
    //         }
    //         else {
    //             console.log("login failed");
    //         }
    //     }
    //     catch(error) {
    //         console.log("Error : ", error.message);
    //     }

    // };

    const handleButtonClick = async () => {
        event.preventDefault(); // Prevents the form from reloading the page

        try {
            console.log("0");
            const response = await fetch("http://localhost:3001/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    username: username,
                    password: password
                })
            });
    
            console.log("1");
            const responseJSON = await response.json();
            console.log("2");
    
            if(responseJSON.success === true) {
                console.log("login succeeded");
                navigate("/homepage");
            } else {
                console.log("login failed");
            }
        } catch (error) {
            console.error("Error during login:", error);
        }
    };
    

    const changeUsername = (event) => {
        setUsername(event.target.value);
    };
    const changePassword = (event) => {
        setPassword(event.target.value);
    };

    return (
        <div>
            <form>
                <input type="text" id="username" value={username} placeholder="Username" onChange={changeUsername}></input>
                <input type="text" id="password" value={password} placeholder="Password" onChange={changePassword}></input>
                <button id="login-button" onClick={handleButtonClick}>Login</button>
            </form>
            <Link to="/register">Register here</Link>
        </div>
    );
}

export default Login;
