import React, {useState, useEffect} from 'react';
import { useLocation} from 'react-router-dom';

const Post = () => {

    const location = useLocation();

    const [comments, setComments] = useState("");




    const fetchComments = async () => {
        try {

            const response = await fetch(`http://localhost:3001/post/${postId}/comments`);
            const data = await response.json();
            return data;
        }
        catch (error) {

        }
    };

    const sortCommentsOnLevel = (dataComments) => {
        // sort the comments on order, so all 0's in front, then 1's, etc
        const sortedDataComments = dataComments.sort((firstComment, secondComment) => {
            if (firstComment.level > secondComment.level) {
                return 1;
            }
            else if (firstComment.level === secondComment.level) {
                return 0;
            }
            else return -1;
        });
        return sortedDataComments;
    }

    const renderComments = (dataComments) => {

        const sortedDataComments = sortCommentsOnLevel(dataComments);

        // first render all level 0 comments
        for (let i = 0; i < sortedDataComments.length; i++) {
            if (sortedDataComments[i].level === 0) {
                // render it
                return renderComment(sortedDataComments[i]);
            }
        }

        // for each element : 
        //      1) find its parent, then 2) create a div box, 3) enter the details of the comment into that div box, 
        // 4) place the div box underneath parent comment


    };

    const renderComment = (comment) => {
        return (
            <div>
                <div id="comment-author">Author : {comment.author}</div>
                <div id = "comment-contents">{comment.description}</div>
            </div>
        );
    };

    return (
        <div>

            <div id="post-title-and-description-section">
                <div id="post-title-div">{location.state.title}</div>
                <div id="post-description-div">{location.state.description}</div>
            </div>

            <div id="comments-section">

            </div>
        </div>
    );
}

export default Post;
