import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


function Login() {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();


    const handleButtonClick = async () => {
        const response = fetch("http://localhost:3001/login", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                username: username,
                password: password
            })
        });

        const responseJSON = await response.JSON();
        if(responseJSON.success === true) {
            console.log("login succeeded");

            navigate("/homepage");
        }
        else {
            console.log("login failed");
        }

    };

    const changeUsername = (event) => {
        setUsername(event.target.value);
    };
    const changePassword = (event) => {
        setPassword(event.target.value);
    };

    return (
        <div>
            <form>
                <input type="text" id="username" value={username} placeholder="Username" onChange={changeUsername}></input>
                <input type="text" id="password" value={password} placeholder="Password" onChange={changePassword}></input>
                <button id="login-button" onClick={handleButtonClick}>Login</button>
            </form>
            <Link to="/register">Register here</Link>
        </div>
    );
}

export default Login;
