import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate, useLocation } from 'react-router-dom';
import "./Comment.css";


const Comment = ({ author, comment, level, id }) => {

    const [replyCommentToPost, setReplyCommentToPost] = useState("");


    const handleReplyToCommentButton = (post) => {

        fetch("http://localhost:3001/post/:id/:commentId", {
            // author, comment, idOfParentPost,
            // level, idOfParentComment, createdAt: formattedDate
            author : author, 
            comment : replyCommentToPost, 
            idOfParentPost : post._id, 
            level : level + 1, 
            idOfParentComment : id,
        })

    };

    const changeReplyCommentToPost = (event) => {
        setReplyCommentToPost(event.target.value);
    };

    return (


        <div>

            <input type="text" id="reply-to-comment-box" value={replyCommentToPost}
                placeholder="Reply to this comment..." onChange={changeReplyCommentToPost}></input>
            <button id="submit-reply-comment-button" onClick={handleSubmitCommentButton}>Submit</button>

            <div id="comment-author">Author : {author}</div>
            <div id="comment-contents">Comment : {comment}</div>

            <button id="reply-to-comment-button">Reply</button>
        </div>
    );
};

export default Comment;
