    username: "",
    setUsername: () => { 
        username : "bob";
    }
});

export const UserProvider = ({ children }) => {
    const [username, password] = useState("");

    return (
        <UserContext.Provider value={{ username, setUsername }}>
            {children}
        </UserContext.Provider>
    );
};
