import React, { useState, useContext } from 'react';
import "./Comment.css";
import UserContext from '../../../context/UserContext';


const Comment = (props) => {
    const [replyCommentToPost, setReplyCommentToPost] = useState("");
    const [showReplyButton, setShowReplyButton] = useState(false);

    const { username, setUsername } = useContext(UserContext);



    const changeReplyCommentToPost = (event) => {
        setReplyCommentToPost(event.target.value);
    };

    const handleClickReplyButton = () => {
        setShowReplyButton(true);
    };

    const handleClickSubmitReplyButton = async (post) => {

        // when button is clicked, 1) make request to post new comment on database, 
        // 2) make post re-fetch all of the data again

        const postId = post.id; // or is it _id?

        // eslint-disable-next-line no-restricted-globals
        const currentCommentId = location.state._id;

        // post the reply
        await fetch(`http://localhost:3001/post/${postId}/${currentCommentId}`, {
            method: "POST",
            header: {},
            body: {
                // { author, comment, idOfParentPost, level, idOfParentComment } 
                author : username,
                comment : replyCommentToPost, 
                idOfParentPost : postId, 
                level : props.level + 1,
                idOfParentComment : currentCommentId
            }
        })

        // make post re-fetch the data
        // const newPostData = await fetch(`http://localhost:3001/post/${postId}`); ///post/:id
        post.handleReplySubmission();

    };

    return (
        <div className="comment-container" style={{ marginLeft: props.level * 20 + 'px' }}>
            <div className="comment-box">
                <div className="comment-author">Author: {props.author}</div>
                <div className="comment-contents">{props.comment}</div>
            </div>


            <div className="reply-section">
                {showReplyButton && (
                    <div>
                        <input
                            type="text"
                            className="reply-input"
                            value={replyCommentToPost}
                            placeholder="Reply to this comment..."
                            onChange={changeReplyCommentToPost}
                        />

                        <button className="submit-reply-button" onClick={handleClickSubmitReplyButton}>
                            Submit
                        </button>
                    </div>
                )
                }

                {!showReplyButton && (
                    <button className="reply-button" onClick={handleClickReplyButton}>
                        Reply
                    </button>
                )}

            </div>
        </div>
    );
};

export default Comment;
